    Transaction tr = session.beginTransaction();

    // 先获取Criteria接口
    Criteria criteria = session.createCriteria(User.class);
    // 没有添加条件，查询所有的数据
    List list = criteria.list();
    System.out.println(list);

    tr.commit();
    session.close();
}
